user.content:
{
	email caddydave82@gmail.com
	log {
		output file /var/log/caddy/access.log
	}
}

(custom_wildcard_tls) {
	tls /etc/caddy/certs/fullchain_wildcard.complexsimplicityai.com.pem /etc/caddy/certs/wildcard.complexsimplicityai.com.key
}

(security_headers) {
	header {
		Strict-Transport-Security max-age=31536000; includeSubDomains; preload
		X-Content-Type-Options nosniff
		X-Frame-Options SAMEORIGIN
		Referrer-Policy same-origin
		-Server
	}
}

http://complexsimplicityai.com {
	redir https://{host}{uri}
}

# Container Management
https://portainer.complexsimplicityai.com {
	import custom_wildcard_tls
	import security_headers
	reverse_proxy https://100.110.82.181:9443 {
		transport http {
			tls_insecure_skip_verify
		}
	}
}

